mapper.insert:
/*[ReplyDao][insertNewReply]*/
INSERT	INTO	RPLY	(
							RPLY_ID
						,	AR_ID		
						,	CONT 		
						,	CRT_DT
						,	MBR_DT	
						,	MBR_ID	
						,	RCMND_CNT
						,	PRNT_RPLY_ID
					)
VALUES				(
							'RP-' || DATE_FORMAT(NOW(), 'YYYYMMDDHH24') || '-' || LPAD(LAST_INSERT_ID(), 6,0)
						, 	#{articleId}
						, 	#{content}
						, 	NOW()
						, 	NOW()
						, 	#{memberId}
						, 	0
						, 	#{parentReplyId}
					)

mapper.update:

UPDATE	RPLY	
SET		RCMND_CNT = RCMND_CNT + 1
WHERE	RPLY_ID	=	#{replyId}
